import styled from 'styled-components';
import { useNavigate, Link } from 'react-router-dom';
import { useUser } from './UserContext';
import logo from '../img/Logo.jpeg';

const HeaderPages = styled.header`
    background-color: #A8B8FF;
    display: flex;
    flex-direction: column;
    height: auto;
    margin-top: -30px;
    margin-left: -30px;
    margin-right: -30px;
    border-radius: 0 0 15px 15px;
`;

const IconUserName = styled.div`
    display: flex;
    flex-direction: row;
    height: fit-content;
    width: fit-content;
    gap: 10px;

    a {
        margin: auto;
        margin-left: -20px;
    }
`;

const NameUserShow = styled.p`
    color: white;
    height: fit-content;
    width: fit-content;

    &:hover {
        color: black;
        text-decoration: underline;
        cursor: pointer;        
    }
`;

const LogoList = styled.div`
    display: flex;
    flex-direction: row;
    padding: auto;
`;

const LogoPages = styled.img`
    margin: 16px;
    width: 100px;
    height: 100px;
    border-radius: 50%;

    &:hover{
        cursor: pointer;
    }
`;

const ListPages = styled.div`
    width: 90%;

    ul {
        list-style: none;
        display: flex;
        gap: 15px;
        padding: 0;
        margin: 40px auto;
    }

    li {
        width: 100%;
        height: auto;
        margin: auto;
        padding: 12px;
        border: 2px solid black;
        border-radius: 5px;
        background-color: #001a9075;

        &:hover {
            background-color: #5271FF;
            box-shadow: 0 0 2px 2px #001a90;
        }
    }

    Link {
        text-decoration: none;
        color: white;

        &:hover {
        color: black;
        }
    }
`;

function Header() {
    const navigate = useNavigate();
    const goTo = (path) => () => navigate(path);
    const { user } = useUser();

    const handleNavegation = (e) => {
        e.preventDefault();
    }

    const handleLogout = (e) => {
        e.preventDefault();

    }

    return (<>
        <HeaderPages>
            <IconUserName>
                <a href="https://wa.me/573212300716" target="_blank">
                    <i className="fa-solid fa-circle-question fa-2x question_icon" style={{color: "black", cursor: "help"}}></i>
                </a>
                <NameUserShow onClick={handleLogout}>
                    {user ? `${user.nombre_usuario} (Cerrar sesiÃ³n)` : 'Invitado (Cerrar sesiÃ³n)'}
                </NameUserShow>
            </IconUserName>
            <LogoList>
                <LogoPages onClick={handleNavegation} src={logo} alt="Logo ALF"/>
                <ListPages>
                    <ul>
                        {user?.id_tipoUsua !== 3 ? (
                            <>
                                <li><Link>Agenda</Link></li>
                                <li>Empleados</li>
                                <li>Equipos</li>
                                <li>Clientes</li>
                                <li>Tipos</li>
                            </>
                        ) : (
                            <li>Lista de servicios asignados</li>
                        )}
                    </ul>
                </ListPages>
            </LogoList>
        </HeaderPages>
    </>);
}

export default Header;
