import React from "react";
import styled from "styled-components";
import { useState } from "react";
import GlobalStyles from '../styles/GlobalStyles';

const ModalOverlay = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 999;
`;

const ModalContent = styled.div`
    background: white;
    padding: 20px;
    border-radius: 8px;
    max-height: 80vh;
    overflow-y: auto;
    width: 80%;
`;

const ModButton = styled.button`
    float: right;
    margin-bottom: 10px;
`;

const Mensaje = styled.p`
    color: ${props => (props.tipo === 'error' ? 'violet' : 'black')};
    text-align: center;
    border: 2px solid ${props => (props.tipo === 'error' ? 'white' : 'black')};
    padding: 10px;
    border-radius: 5px;
    background-color: ${props => (props.tipo === 'mensaje' ? 'rgba(255, 255, 255, 0.4)' : 'rgba(0, 0, 0, 0.4)')};
`;

const LoginForm = styled.form`
    width: 20%;
    min-width: 300px;
    margin: 20px auto;
    display: flex;
    flex-direction: column;
    padding-top: 15px;
    gap: 15px;
    border: 5px solid rgba(255, 255, 255, 0.089);
    border-radius: 15px;
    background-color:rgba(37, 94, 250, 0.51);

    i {
    margin-top: 15px;
    margin-bottom: 6px;
    color: black;
    }

    input {
    align-self: center;
    padding: 5px;
    min-width: 100px;
    width: 80%;
    max-width: 250px;
    height: 16px;
    margin-left: 5%;
    margin-right: 5%;
    border: 2px solid black;
    border-radius: 5px;
    background-color: #ffffff6b; /* Blanco con transparencia */
    }

    button {
    align-self: center;
    padding: 5px;
    min-width: 100px;
    }

    div {
    flex-direction: row;
    }
`;

const registrar = () => {

};

function ModalRegister({onClose}){
    const defStatusNuevoUser = {nombre_usuario: '', contrasena: ''}
    const [nuevoUsuario, setNuevoUsuario] = useState(defStatusNuevoUser);


    return (<>
        <GlobalStyles/>
        <ModalOverlay>
            <ModalContent>                
                <h2 style={{color: 'black'}}>Registro</h2>
                <LoginForm>
                    <label htmlFor="nombre_usuario">Usuario</label>
                    <input
                        type="text"
                        name="nombre_usuario"
                        id="nombre_usuario"
                        value={nuevoUsuario.nombre_usuario}
                        onChange={(e) => setNuevoUsuario(nuevoUsuario => ({ ...nuevoUsuario, nombre_usuario: e.target.value.trimStart()}))}
                    />
                    <label htmlFor="contrasena">Contrase√±a</label>
                    <input
                        type="password"
                        name="contrasena"
                        id="contrasena"
                        value={nuevoUsuario.contrasena}
                        onChange={(e) => setNuevoUsuario(contrasena => ({ ...contrasena, contrasena: e.target.value.trimStart()}))}
                    />
                    <div>
                        <ModButton>Registro</ModButton>
                        <ModButton onClick={onClose}>Cerrar</ModButton>
                    </div>
                </LoginForm>                
            </ModalContent>
        </ModalOverlay>
    </>);
};

export default ModalRegister;
