import { createGlobalStyle } from 'styled-components';

const GlobalStyles = createGlobalStyle`
    body {
        font-family: Arial, sans-serif;
        background-color: #5271FF;
        color: black;
        padding: 20px;
        text-align: center;
        min-height: 95vh;
        display: flex;
        flex-direction: column;
    }

    main {
        flex: 1;
    }

    button {
        margin: 5px;
        align-self: center;
        width: fit-content;
        min-width: 40px;
        width: 120px;
        height: 35px;
        border: 1px solid black;
        background-color: #001A90; /* Azul oscuro */
        color: white;
        padding: 1px;
        border-radius: 5px;
    }
`;


export default GlobalStyles;
