import { useState, useEffect } from "react";
import { useUser } from "../components/UserContext";
import { useNavigate } from "react-router-dom";
import { getFechaHoraActual } from '../utils/time';
import axios from 'axios';
import styled from 'styled-components';
import GlobalStyles from '../styles/GlobalStyles';

const ModalOverlay = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 999;
`;

const ModalContent = styled.div`
    background: white;
    padding: 20px;
    border-radius: 8px;
    max-height: 80vh;
    overflow-y: auto;
    width: 80%;
`;

const CloseButton = styled.button`
    float: right;
    margin-bottom: 10px;
`;

const Table = styled.table`
    width: 100%;
    border-collapse: collapse;

    th, td {
        padding: 8px;
        border: 1px solid #ccc;
        text-align: center;
    }

    th {
        background-color: #f4f4f4;
    }
`;

function ViewServicios() {
    document.title = "Servicios asignados | AMM";
    const { user } = useUser();
    const navigate = useNavigate();

    const [servicios, setServicios] = useState([]);
    const [clientes, setClientes] = useState([]);
    const [equipos, setEquipos] = useState([]);
    const [usuariosEquipo, setUsuariosEquipo] = useState([]);
    const [tiposDoc, setTiposDoc] = useState([]);
    const [datosModal, setDatosModal] = useState([]);

    const columnas = ['Cliente', 'Dirección', 'Equipo', 'Limpieza', 'Hora', 'Tiempo de servicio', 'Observación'];

    //cargar datos
    useEffect(() => {
        cargarDatos();
    }, []);

    const cargarDatos = async () => {
        try {
            const [resClient, resEquip, resUsEq, resTLimp] = await Promise.all([
                axios.get('http://localhost:3001/clientes'),
                axios.get('http://localhost:3001/equipos'),
                axios.get('http://localhost:3001/equipos/usuarios'),
                axios.get('http://localhost:3001/tipos/limpieza')
            ]);
            setClientes(resClient.data);
            setEquipos(resEquip.data);
            setUsuariosEquipo(resUsEq.data);
            setTiposDoc(resTLimp.data);

            const equipoDeUser = usuariosEquipo.filter(emp => emp.id_usuario === user.id_usuario);

            console.log('Equipos del usuario', equipoDeUser);

        } catch (error) {
            console.error('Error en la carga de datos ', error);
        }
    }

    const onBack = () => {
        navigate('/Inicio');
    };

    return (<>
        <GlobalStyles/>
        <ModalOverlay>
            <ModalContent>
                <CloseButton onClick={onBack}>Regresar</CloseButton>                
                <h2 style={{color: 'black'}}>Servicios del día</h2>
                <Table>                    
                    <thead>
                        <tr>
                            {columnas.map(({label}) => (
                                <th key={label}>{label}</th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>
                        {datosModal.map((item, index) => (
                            <tr key={index}>
                                {columnas.map(({field}) => (
                                    <td key={field}>{item[field]}</td>
                                ))}                                
                            </tr>
                        ))}                        
                    </tbody>
                </Table>
            </ModalContent>
        </ModalOverlay>
    </>);
};

export default ViewServicios;
