import { useState, useEffect } from "react";
import { useUser } from "../components/UserContext";
import { useNavigate } from "react-router-dom";
import { getFechaHoraActual } from '../utils/time';
import ButtonActions from '../components/ButtonActions';
import Header from '../components/Header';
import axios from 'axios';
import styled from 'styled-components';
import GlobalStyles from '../styles/GlobalStyles';
import ModalTable from '../components/ModalTable';

const Mensaje = styled.p`
    color: ${props => (props.tipo === 'error' ? 'darkred' : 'black')}
    text-align: center;
    border: 2px solid ${props => (props.tipo === 'error' ? 'white' : 'black')};
    padding: 10px;
    border-radius: 5px;
    background-color: ${props => (props.tipo == 'mensaje' ? 'rgba(255, 255, 255, 0.4)' : 'rgba(0, 0, 0, 0.4)')};
`;

const PagesDiv = styled.div`
    min-height: 80vh;
    margin-top: 5px;
    height: auto;
    width: auto;
    align-self: center;
    align-items: center;
`;

function Clientes() {
    document.title = "Clientes | AMM";
    const { user } = useUser();
    const navigate = useNavigate();

    const [error, setError] = useState('');
    const [mensaje, setMensaje] = useState('');

    const [clientes, setClientes] = useState([]);
    const [nuevoClient, setNuevoClient] = useState({id_cliente: '', nombre_cliente: '', apellido_cliente: '', direccion_cliente: '', telefono_cliente: '', correo_cliente: '', observacion_cliente: '', user_crea: '', user_modifica: '', modificado_el: ''});

    //cargar datos
    useEffect(() => {
        cargarDatos();
    }, []);

    const cargarDatos = async () => {
        try {
            const resClient = await Promise.all(
                axios.get('http://localhost:3001/clientes')
            );
            setClientes(resClient.data);
        } catch (error) {
            console.error('Error en la carga de datos ', error);
        }
    }

    const handleCliente = async (e) => {
        e.preventDefault();
        setMensaje('');
        setError('');

        if(!user || user.id_tipoUsua === 3){
            navigate('/');  
        }
    };

    return(<>
        <GlobalStyles />
        <Header />
        <PagesDiv>
            <div>
                {/* Mensajes */}
                {error && <Mensaje tipo="error">{error}</Mensaje>}
                {mensaje && <Mensaje tipo="mensaje">{mensaje}</Mensaje>}
                <form className="form_pages" onSubmit={handleCliente}>
                    <div className="form_display">
                        <label htmlFor="id_cliente">Código</label>
                        <select 
                            name="id_cliente"
                            id="id_cliente"
                            value={nuevoClient.id_cliente}
                            onChange={(e) => setNuevoClient(nuevoClient => ({ ...nuevoClient, id_cliente: e.target.value}))}
                        >
                            <option key="NA" value="NA">Nuevo registro</option>
                            {clientes.map(client => (
                                <option key={client.id_cliente} value={client.id_cliente}>
                                    {client.id_cliente}
                                </option>
                            ))}
                        </select>
                        <label htmlFor="nombre_cliente">Nombres</label>
                        <input
                            type="text"
                            name="nombre_cliente"
                            id="nombre_cliente"
                            value={nuevoClient.nombre_cliente}
                            onChange={(e) => setNuevoClient(nuevoClient => ({ ...nuevoClient, nombre_cliente: e.target.value}))}
                        />
                        <label htmlFor="apellido_cliente">Apellidos</label>
                        <input
                            type="text"
                            name="apellido_cliente"
                            id="apellido_cliente"
                            value={nuevoClient.apellido_cliente}
                            onChange={(e) => setNuevoClient(nuevoClient => ({ ...nuevoClient, apellido_cliente: e.target.value}))}
                        />
                        <label htmlFor="direccion_cliente">Dirección</label>
                        <input
                            type="text"
                            name="direccion_cliente"
                            id="direccion_cliente"
                            value={nuevoClient.direccion_cliente}
                            onChange={(e) => setNuevoClient(nuevoClient => ({ ...nuevoClient, direccion_cliente: e.target.value}))}
                        />
                        <label htmlFor="telefono_cliente">Dirección</label>
                        <input
                            type="text"
                            name="telefono_cliente"
                            id="telefono_cliente"
                            value={nuevoClient.telefono_cliente}
                            onChange={(e) => setNuevoClient(nuevoClient => ({ ...nuevoClient, telefono_cliente: e.target.value}))}
                        />
                        <label htmlFor="correo_cliente">Correo electronico</label>
                        <input
                            type="text"
                            name="correo_cliente"
                            id="correo_cliente"
                            value={nuevoClient.correo_cliente}
                            onChange={(e) => setNuevoClient(nuevoClient => ({ ...nuevoClient, correo_cliente: e.target.value}))}
                        />
                        <label htmlFor="observacion_cliente">Observaciones</label>
                        <input
                            type="text"
                            className="observaciones"
                            name="observacion_cliente"
                            id="observacion_cliente"
                            value={nuevoClient.observacion_cliente}
                            onChange={(e) => setNuevoClient(nuevoClient => ({ ...nuevoClient, observacion_cliente: e.target.value}))}
                        />
                    </div>
                    <ButtonActions/>
                </form>
            </div>
        </PagesDiv>
    </>)
};

export default Clientes;
