import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import GlobalStyles from '../styles/GlobalStyles';
import logo from '../img/Logo.jpeg';

const HeaderQuestion = styled.header`
    text-align: right;
    margin: -20px;
`;

const DivLoginLogo = styled.div`
    margin: 15px;
`;

const LoginLogo = styled.img`
    border: 4px solid black;
    width: 100%;
    max-width: 300px;
    border-radius: 25%;
`;

const LoginForm = styled.form`
    width: 20%;
    min-width: 300px;
    min-height: 300px;
    margin: 20px auto;
    display: flex;
    flex-direction: column;
    border: 5px solid rgba(255, 255, 255, 0.089);
    border-radius: 15px;
    background-color: #80a1fd81;

    i {
    margin-top: 15px;
    margin-bottom: 6px;
    color: black;
    }

    input {
    align-self: center;
    padding: 5px;
    min-width: 100px;
    width: 80%;
    max-width: 250px;
    height: 16px;
    margin-left: 5%;
    margin-right: 5%;
    border: 2px solid black;
    border-radius: 5px;
    background-color: #ffffff6b; /* Blanco con transparencia */
    }

    button {
    align-self: center;
    padding: 5px;
    min-width: 100px;
    margin-top: 15%;
    }
`;

const FooterLogin = styled.footer`
    margin-top: 5px;
`;

const Mensaje = styled.p`
    color: ${props => (props.tipo === 'error' ? 'darkred' : 'black')}
    text-align: center;
`;

function Login() {
    const navigate = useNavigate();
    document.title = "Login | AMM";
    const [user, setUser] = useState('');
    const [pass, setPass] = useState('');
    const [error, setError] = useState('');
    const [mensaje, setMensaje] = useState('');

    const handleLogin = (e) => {
        e.preventDefault();
        //logica        
    };

    return (<>  
        <GlobalStyles/>
        <main>
            <HeaderQuestion>
                <i className="fa-solid fa-circle-question fa-3x" style={{color: "black"}}></i>
            </HeaderQuestion>
            <DivLoginLogo>
                <LoginLogo src={logo} alt="Logo ALF" />
            </DivLoginLogo>
            <LoginForm onSubmit={handleLogin}>
                <i className="fa-solid fa-user fa-2x"></i>
                <input type="text" id="user" name="user" placeholder="Usuario" required />
                <i className="fa-solid fa-lock fa-2x"></i>
                <input type="password" name="pass" id="pass" placeholder="ContraseÃ±a" required />

                <button type="submit">Ingresar</button>
                {/* Mensajes */}
                {error && <Mensaje tipo="error">{error}</Mensaje>}
            </LoginForm>
        </main>
    </>
    );
};

export default Login;
