import Header from '../components/Header';
import styled from 'styled-components';
import GlobalStyles from '../styles/GlobalStyles';
import { useUser } from "../components/UserContext";
import { useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';
import { fechaTitle, formatearFecha } from '../utils/time';

const InicioPrimero = styled.div`
    margin: 10px;
    width: 100%;
    min-height: 75vh;
    align-self: center;
    display: flex;
    flex-direction: row;
    gap: 50px;
`;

const StartData = styled.div`
    margin: 40px auto;

    div, h1 {
        min-width: 100px;
    }
`;

const ColumStart = styled.div`
    flex-direction: row;
    padding: 5px;
    background-color: #001a9049;
    color: white;
    min-width: 200px;
    height: 85%;
    border-radius: 10px;
    box-shadow: 6px 6px 10px 5px #001A90;

    div {
        padding: 10px;
        margin: 5px;
        width: auto;
        margin-bottom: 10px;
        background-color: rgba(0, 0, 0, 0.377);
        border: 1px solid white;
        border-radius: 10px;
    }
`;



function Inicio(){
    document.title = "Inicio | AMM";
    
    const { user } = useUser();
    const navigate = useNavigate();
    const [servicios, setServicios] = useState([]);
    const [clientes, setClientes] = useState([]);
    const [equipos, setEquipos] = useState([]);
    const [tiposLimpieza, setTiposLimpieza] = useState([]);
    const [serviciosMostrar, setServiciosMostrar] = useState([]);
    const [serviciosToday, setServiciosToday] = useState([]);
    const [serviciosTomorrow, setServiciosTomorrow] = useState([]);

    if(!user){
        navigate('/');  
    }

    //cargar datos
    useEffect(() => {
        cargarDatos();
    }, []);

    const cargarDatos = async () => {
        try {
            const [resServ, resClient, resEquip, resTLimp] = await Promise.all([
                axios.get('http://localhost:3001/servicios'),
                axios.get('http://localhost:3001/clientes'),
                axios.get('http://localhost:3001/equipos'),
                axios.get('http://localhost:3001/tipos/limpieza')
            ]);
            setServicios(resServ.data);
            setClientes(resClient.data);
            setEquipos(resEquip.data);
            setTiposLimpieza(resTLimp.data);
        } catch (error) {
            console.error('Error en la carga de datos ', error);
        }

        servicios.map(serv => {
            const cliente = clientes.find(u => u.id_cliente === serv.id_cliente);
            const equipo = equipos.find(u => u.id_equipo === serv.id_equipo);
            const tipoLimp = tiposLimpieza.find(u => u.id_tipoLimp === serv.id_tipoLimp);

            setServiciosMostrar({
                ...serviciosMostrar,
                id_servicio: serv.id_servicio,
                nombre_cliente: cliente.nombre_cliente + ' ' + cliente.apellido_cliente,
                direccion_cliente: cliente.direccion_cliente,
                nombre_equipo: equipo.nombre_equipo,
                nombre_tipo_limp: tipoLimp.nombre_tipo,
                fecha: serv.fecha,
                hora: serv.hora,
                tiempo_estimado: serv.tiempo_estimado,
                tiempo_finalizacion: serv.tiempo_finalizacion,
                precio: serv.precio,
                observacion: serv.observacion
            })
        });

        const today = new Date();
        const tomorrow = new Date(today);
        tomorrow.setDate(today.getDate() + 1);

        const fechaHoy = formatearFecha(today);
        const fechaMa = formatearFecha(tomorrow);

        setServiciosToday(serviciosMostrar.filter(serv => serv.fecha === fechaHoy));
        setServiciosTomorrow(serviciosMostrar.filter(serv => serv.fecha === fechaMa));
    }

    return (<>
        <GlobalStyles/>
        <Header/>
        <InicioPrimero>
            <StartData>
                <div>
                    <h1>Limpieza de {fechaTitle(new Date())}</h1>
                </div>
                <ColumStart>
                <div>
                    {!serviciosToday ? (
                        <p>No hay servicios para mostrar</p>
                    ) : serviciosToday.map(serv => (<>
                        <p>Código: {serv.id_servicio}</p>
                        <p>Cliente: </p>
                        <p>Dirección: </p>
                        <p>Equipo: </p>
                        <p>Limpieza: </p>
                        <p></p>
                        <p></p>
                        <p></p>
                        <p></p>
                        <p></p>
                        <p></p>
                    </>))
                    }
                </div>
            </ColumStart>
            </StartData>            
            <StartData>
                <div>
                    <h1>Limpiezas {fechaTitle(new Date().setDate(new Date().getDate() + 1))}</h1>
                </div>
                <ColumStart>
                    <div>
                        <p>fecha</p>
                    </div>
                </ColumStart>
            </StartData>
        </InicioPrimero>
    </>);
}

export default Inicio;
