import React from "react";
import styled from "styled-components";

const ModalOverlay = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 999;
`;

const ModalContent = styled.div`
    background: white;
    padding: 20px;
    border-radius: 8px;
    max-height: 80vh;
    overflow-y: auto;
    width: 80%;
`;

const ModButton = styled.button`
    float: right;
    margin-bottom: 10px;
`;

const Mensaje = styled.p`
    color: ${props => (props.tipo === 'error' ? 'violet' : 'black')};
    text-align: center;
    border: 2px solid ${props => (props.tipo === 'error' ? 'white' : 'black')};
    padding: 10px;
    border-radius: 5px;
    background-color: ${props => (props.tipo === 'mensaje' ? 'rgba(255, 255, 255, 0.4)' : 'rgba(0, 0, 0, 0.4)')};
`;

const PagesDiv = styled.div`
    min-height: 80vh;
    margin-top: 5px;
    height: auto;
    width: auto;
    align-self: center;
    align-items: center;
`;

const registrar = () => {

};

function ModalRegister({onClose}){
    return (
        <ModalOverlay>
            <ModalContent>
                <ModButton onClick={onClose}>Cerrar</ModButton>
                <h2 style={{color: 'black'}}>Registro</h2>
                <ModButton onClick={onClose}>Registro</ModButton>
            </ModalContent>
        </ModalOverlay>
    );
};

export default ModalRegister;
