import React from 'react';

function ButtonActions({onAction}) {
    const acciones = [
        { label: 'Buscar', value: 'buscar'},
        { label: 'Crear/Modificar', value: 'crear/modificar'},
        { label: 'Mostrar registros', value: 'mostrar'},
        { label: 'Eliminar', value: 'eliminar'}
    ];

    return (<div>
        {acciones.map(({label, value}) => (
            <button key={value} type="button" onClick={() => onAction(value)}>
                {label}
            </button>
        ))}
    </div>);
};

export default ButtonActions;
